insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('1', '1', 'Aluminum Pulley And Belt Kit/Short Water Pump', 'Small block chevy aluminum pulley and belt kit for short water pump, 25% reduction, upper pulley fits 5/8\" or 3/4\" water pump shaft. Kit includes 2 groove upper and 2 groove lower pulley and pair of belts.', '', '28');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('344', '1', 'Wilwood-Clutch Slave Cyinder', 'Pull style clutch slave Cylinder. (Use with 3/4\" master cylinder)', '', '27');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('4', '1', 'Aluminum Pulley And Belt Kit/Long Water Pump', 'Small block chevy aluminum pulley and belt kit for long water pump, 25% reduction, upper pulley fits 5/8\" or 3/4\" water pump shaft. Kit includes 2 groove upper and 2 groove lower pulley and pair of belts', '', '34');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('343', '1', 'Wilwood 3/4\" Aluminum Master Cyinder', 'Aluminum 3/4\" master cyinder, ideal for operating clutch slave cyinders.', '', '20');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('17', '1', 'Bilstein-Street Stock Shock GM Front', 'Bilstein AK 1043 Shock, mono-tube gas pressure shock, IMCA approved. Bilstein shocks will not fade like ordinary twin tube shocks. Fits front of 73 - 83 Chevelle, 73 - 88 Monte Carlo, Buick Regal.', '', '32');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('18', '1', 'Bilstein-Street Stock Shock GM Rear', 'Bilstein AK 1044 Shock, mono-tube gas pressure shock. IMCA approved. Bilstein shocks will not fade like ordinary twin tube shocks. Fits rear of 73 - 83 Chevelle, 73 - 88 Monte Carlo, Buick Regal.', '', '29');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('7', '1', '350 Chevy - Dart Iron Eagle SS 67cc 2.02/ 1.60 Cylinder Heads', 'Pair of assembled Dart SS cylinder heads for carbureted small block 327, 350, 377, 383 or 400 engines. (377 & 400 applications for street would require steam holes to be drilled) 67cc, heart shaped combustion chambers. Straight plug design. High flow 170cc intake runners. Integral valve guides. These heads are assembled in our machine shop. Seat concentricy is checked, mild bowl blending is done to remove any casting intrusions. Assembled with premium stainless swirl polished 2.02 intake valves and 1.60 exhaust valves with undercut stems for improved flow. New .550 lift Z-28 valve springs, new steel retainers, hardened locks, and umbrella valve seals. Screw in rocker arm studs (3/8\") and guideplates are also included. ( Dual springs w/damper option is available for an additional $80.00. Dual springs are 1.44 O.D. with 135# of seat pressure and will handle up to .600 lift. Dual spring option also includes chrome moly retainers and machined valve locks.)All machine work is done in our own shop on quality equipment.', '', '478');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('8', '1', 'Pistons - 383 Flat Top - Coated Skirts-Balanced Set', 'Set of 8 Speed Pro #H860CP hypereutectic pistons. Piston set is fully balanced in our machine shop to within 1 gram. Teflon coated skirts. Flat top pistons with 2 valve reliefs. 5/64\", 5/64\", 3/16\" ring grooves. For use with 5.7\" length rods. Compression with 64cc heads is approx. 10 to 1, with 76cc heads is approx. 9 to 1. Includes snap rings, pistons can be pressed or full floated. Gram weights between sets vary but are approx. 535 grams piston weight.', '', '73');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('9', '1', 'Pistons - 383 - .200 Dome-Coated Skirts-Balanced Set', 'Set of 8 Speed Pro #H635CP pistons. Piston set is fully balanced in our machine shop to within 1 gram. Teflon coated skirts. Pistons have .200 dome. 5/64\", 5/64\", 3/16\" ring grooves. For use with 5.7 length rods. Compression with 64cc heads is approx. 12.4 to 1, with 76cc heads is approx. 10.7 to 1. Includes snap rings, pistons can be pressed or full floated. Gram weights between sets vary buy are approx. 545 grams piston weight and 160 grams pin weight.', '', '60');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('10', '1', '350 Chevy 882 Performance Heads 76cc -  2.02/1.60', 'Pair of casting #882 heads with intake valve size increased to 2.02, 1.60 exhaust valves. Approx. 76cc combustion chamber. These heads will work on the street with unleaded fuel and have bolt holes on ends of heads for all accessories. These heads are for carbureted engines. Three angle valve job, surfaced, new valve guides, assembled with new premium stainless 2.02 intake valves and 1.60 exhaust valves with necked down stems. New .550 lift Z-28 valve springs, new steel retainers and hardened valve locks, umbrella valve stem seals. Heads have 3/8\" press in rocker studs. All machine work is done in our own shop on quality equipment.', '', '124');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('14', '1', 'Steering Wheel - Steel', 'Steel steering wheel, 3\" dish, your choice of 15\" or 17\" diameter.', '', '24');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('15', '1', 'SB Chevy CT Valve Covers-Gold', 'Pair of steel, SBC tall circle track valve covers. Tall enough to clear stud girdles. Two baffeled 1 1/2\" breather tubes on one valve cover.', '', '28');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('19', '1', 'SBC Prof. Products Satin Finish Hurricane', 'Small block chevy Professional Products Hurricane Intake Manifold. Single plane high rise manifold that has proven to be a good performer. Basic rpm range is 3500-8500. Satin finish. Fits most GM and aftermarket 23 degree heads.', '', '60');
