insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('519', '1', 'Pistons - 400 Flat Top - Coated Skirts-Balanced Set', 'Set of 8 Speed Pro #H616CP hypereutectic pistons. Piston set is fully balanced in our machine shop to within 1 gram. Teflon coated skirts. Flat top pistons with 4 valve reliefs. 5/64\", 5/64\", 3/16\" ring grooves. For use with 5.7\" length rods. Compression with 64cc heads is approx. 10.8 to 1, with 76cc heads is approx. 9.6 to 1. Includes snap rings, pistons can be pressed or full floated. Gram weights between sets vary but are approx. 593 grams piston weight.', '', '3');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('520', '1', 'Pistons - 400 - .100 Dome-Coated Skirts-Balanced Set', 'Set of 8 Speed Pro # H623CP pistons. Piston set is balanced in our machine shop to within 1 gram. Teflon coated skirts. Pistons have .100 dome. 5/64\", 5/64\", 3/16\" ring grooves. For use with 5.7\" length rods. Compression with 64cc heads is approx. 12.1 to 1, with 76cc heads is approx. 10.5 to 1. Includes snap rings, pistons can be pressed or full floated. Gram weights between sets vary but are approx. 569 grams piston weight.', '', '7');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('521', '1', 'Pistons - 350 - .125 Dome-Coated Skirts-Balanced Set', 'Set of 8 Speed Pro # H618CP pistons. Piston set is balanced in our machine shop to within 1 gram. Teflon coated skirts. Pistons have .125 dome. 5/64\", 5/64\", 3/16\" ring grooves. For use with 5.7\" length rods. Compression with 64cc heads is approx. 10.7 to 1, with 76cc heads is approx. 9.4 to 1. Includes snap rings, pistons can be pressed or full floated. Gram weights between sets vary but are approx. 596 grams piston weight.', '', '7');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('522', '1', 'Pistons - 350 - .275 Dome-Coated Skirts-Balanced Set', 'Set of 8 Speed Pro # H617CP pistons. Piston set is balanced in our machine shop to within 1 gram. Teflon coated skirts. Pistons have .275 dome. 5/64\", 5/64\", 3/16\" ring grooves. For use with 5.7\" length rods. Compression with 64cc heads is approx. 11.9 to 1, with 76cc heads is approx. 10.3 to 1. Includes snap rings, pistons can be pressed or full floated. Gram weights between sets vary but are approx. 616 grams piston weight.', '', '3');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('523', '1', 'Pistons - 350 - .220 Dome Forged Pistons - Coated Skirts - Balan', 'Set of 8 Speed Pro # L2252AF Forged pistons. CNC machined. Teflon coated skirts. Piston set is fully balanced in our machine shop to within 1 gram. Pistons have .220 dome. 1/16\", 1/16\", 1/8\" ring grooves. For use with 5.7\" rods. Compression with 64cc heads is approx. 11.9 to 1, with 76cc heads is approx. 10.2 to 1. Pistons are for press fit or full floating rods and include snap rings. Gram weights between sets vary but are approx. 599 grams piston weight.', '', '1');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('524', '1', 'Pistons - 383 - 6IN Rod - Coated Skirts - Balanced Set', 'Set of 8 Speed Pro #H124CL lightweight CNC machined 383 Chevy hypereutectic flat top pistons with 2 valve reliefs. DurOShield coated skirts. Piston set is fully balanced in our machine shop to within 1 gram. For use with 6.0\" rods. 1/16\", 1/16\", 3/16\" ring grooves. Compression with 64cc heads is approx. 10.5 to 1, with 76cc heads is approx. 9.3 to 1. Includes snap rings, pistons can be pressed or full floated. Gram weights between sets vary but are approx. 560 grams piston weight and 126 grams pin weight.', '', '4');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('525', '1', 'Impact - Mini Champ Youth Helmet', 'Impact Racing Mini Champ Youth helmet. Made in the USA. Finding and fitting a helmet for your constantly growing 5 to 10 year old racer just got simpler. The Mini Champ youth helmet with Quick-Fit Inserts is designed to grow with your young racer all the way to adult size. The Quick-Fit memory foam sizing pads (included) makes fitting and refitting the Mini Champ fast and easy. A single Mini Champ helmet fits youth sizes small, medium and large. You can even custom shape the inserts for hard to fit or in between sizes. The Mini Champ youth helmets are SFI 24.1 approved (youth helmet standard) for QMA/WKA/NHRA/IKF use. Available in silver only.', '', '4');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('526', '1', 'Impact - Charger Helmet - Silver', 'Impact Racing Charger helmet. (Charger is also available in black.) Made in the USA. Snell SA2005 rated. The Charger features a oversized wrap around eyeport for wide angle panoramic vision. Impact\'s computer generated shell and liner are matched to offer a secure, comfortable and consistent fit. Something you will appreciate every time you put the helmet on. Available in   sizes Small - 6 7/8\"-7\", Medium - 7 1/8\"-7 1/4\", Large - 7 3/8\"-7 1/2\", X-Large - 7 5/8\"-7 3/4\".', '', '1');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('527', '1', 'Impact - Charger Helmet - Black', 'Impact Racing Charger helmet. (Charger is also available in silver.) Made in the USA. Snell SA2005 rated. The Charger features a oversized wrap around eyeport for wide angle panoramic vision. Impact\'s computer generated shell and liner are matched to offer a secure, comfortable and consistent fit. Something you will appreciate every time you put the helmet on. Available in sizes Small - 6 7/8\"-7\", Medium - 7 1/8\"-7 1/4\", Large - 7 3/8\"-7 1/2\", X-Large - 7 5/8\"-7 3/4\".', '', '4');
insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('528', '1', 'Impact - Champ Helmet - Black', 'Impact Racing Champ helmet. (Champ helmet is also available in silver.) Made in the USA. Snell SA2005 rated. The Champ features a radical chinbar design that extends coverage over the mouth and nose and acts as a breath deflector. The expanded eyeport is designed to work in every racing cockpit. Available in sizes Small - 6 7/8\"-7\", Medium - 7 1/8\"-7 1/4\", Large - 7 3/8\"-7 1/2\", X-Large - 7 5/8\"-7 3/4\".', '', '4');
