insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('530', '1', 'Impact - Super Sport Helmet - Black', 'Impact Racing Super Sport helmet. (Super Sport also available in silver.) Made in the USA. Snell SA2005 rated. From the wide eyeport design to the perfect fit and feel the Super Sport is at home in any race car cockpit. Available in sizes Small - 6 7/8\"-7\", Medium - 7 1/8\"-7 1/4\", Large - 7 3/8\"-7 1/2\", X-Large - 7 5/8\"-7 3/4\".', '', '8');
